const { useState, createContext } = require("react");

export const NumberContext = createContext();

function Products() {
  const productData = [
    {
      title: "Prada Panther",
      description: "This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.",
      image: pr3,
      Price: "Rs. 5000",
    },
    {
      title: "Gangster's Paradise",
      description: "This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.",
      image: pr2,
      Price: "Rs. 8000",
    },
    {
      title: "AL PACINO AND ROBERT DE NIRO",
      description: "This is a longer card with supporting text below as a natural lead-in to additional content.",
      image: Pr1,
      Price: "Rs. 3000",
    },
    {
      title: "Scarface",
      description: "This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.",
      image: pr4,
      Price: "Rs. 5000",
    },
    {
      title: "Scarface Supreme",
      description: "This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.",
      image: pr5,
      Price: "Rs. 4500",
    },
    {
      title: "The GodFather",
      description: "This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.",
      image: pr6,
      Price: "Rs. 6500",
    },
  ];


  return (
    <>
     <div className="container">
    <div  className="row row4">
  <div class="row row-cols-1 row-cols-md-3 g-4">
  {data.map((Canvasdata) => (
    <div class="col">
    <div class="card">
      <img src={Canvasdata.image} class="card-img-top" alt={Canvasdata.title}/>
      <div class="card-body">
        <h5 class="card-title">{Canvasdata.title}</h5>
        <p class="card-text" style={{textAlign:"center"}}>{Canvasdata.Price}</p>
       <div className='container'> <button className="btn btn-primary">Add to Cart</button></div>
      </div>
    </div>
  </div>
  ))}
</div>
</div>
</div>

    </>
  );
}

export default Products;
