import React, { useState, useEffect } from 'react';
import { deleteproduct, getproduct } from '../Services/api';
import './products.css';

export default function Productview() {
  const [data, setData] = useState([]);

  useEffect(() => {
    // Define a flag to ensure API call only happens on first load
    let isMounted = true;

    // Fetch data only when the component is mounted
    if (isMounted) {
      fetchdata();
    }

    // Cleanup function to update the flag when the component unmounts
    return () => {
      isMounted = false;
    };
  }, []);

  const fetchdata = async () => {
    const productdetail = await getproduct();
    setData(productdetail);
  };

  const handledeleteitem = async (productId) => {
    await deleteproduct(productId);
    // After deleting, update the data by refetching or modifying the existing data
    // For example, you can filter out the deleted item from 'data'
    setData((prevData) => prevData.filter((item) => item.id !== productId));
  };
    return (
        <>
        <div className="container">
          <div className="row row4">
            <div class="row row-cols-1 row-cols-md-3 g-4">
              {data.map((item) => (
                <div class="col col-md-3 " style={{padding:"15px"}} key={item.id}>
                  <div class="card">
                    <img src={item.img} class="card-img-top cardimg" alt={item.name} />
                    <div class="card-body">
                      <h5 class="card-title"style={{ textAlign: "center" }}>{item.title}</h5>
                      <p class="card-text" style={{ textAlign: "center" }}>{item.price}</p>
                      <div className='container'> <button className="btn btn-outline-dark" onClick={()=>handledeleteitem(item.id)}>Delete</button></div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
  
      </>
  )
}
