import React, { useState, useEffect } from 'react';
import { deleteproduct, getproduct } from '../Services/api';
import './products.css';

export default function Productview() {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const productDetail = await getproduct();
        setData(productDetail);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleDeleteItem = async (productId) => {
    try {
      await deleteproduct(productId);
      setData((prevData) => prevData.filter((item) => item.id !== productId));
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <>
      <h1>Products</h1>
      <div className="container">
        <div className="row row4">
          <div className="row row-cols-1 row-cols-md-3 g-4">
            {data.map((item) => (
              <div className="col col-md-3" style={{ padding: '15px' }} key={item.id}>
                <div className="card">
                  <img src={item.img} className="card-img-top cardimg" alt={item.name} />
                  <div className="card-body">
                    <h5 className="card-title" style={{ textAlign: 'center' }}>{item.title}</h5>
                    <p className="card-text" style={{ textAlign: 'center' }}>{item.price}</p>
                    <div className='container'>
                      <button className="btn btn-outline-dark" onClick={() => handleDeleteItem(item.id)}>
                        Delete
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}
