import React, { useState } from 'react';
import {data} from './data'; // Import the product data from data.js

const ProductList = () => {
  const [selectedCategory, setSelectedCategory] = useState('');
  
  const filteredProducts = data.filter(product => product.category === selectedCategory);

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div>
      <h1>Product Categories</h1>
      <div>
        <button onClick={() => handleCategoryChange('Mafia')}>Mafia</button>
        <button onClick={() => handleCategoryChange('Anime')}>Anime</button>
        <button onClick={() => handleCategoryChange('Cars')}>Cars</button>
      </div>
      <h2>{selectedCategory} Products</h2>

        <div className="row row4">
          <div class="row row-cols-1 row-cols-md-3 g-4">
            {filteredProducts.map((product) => (
              <div class="col" key={product.id}>
                <div class="card cardsize container">
                  <img src={product.image} class="card-img-top cardimg" alt={product.title} />
                  <div class="card-body">
                    <h5 class="card-title">{product.title}</h5>
                    <p class="card-text" style={{ textAlign: "center" }}>{product.Price}</p>
                    <div className='container'><button className="btn btn-primary">Add to Cart</button></div>
                    <div><input type="number"></input> </div>
                    
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
 
  );
};

export default ProductList;


















