import "./reviews.css";
import { useState } from "react";
import { Link } from "react-router-dom";
 
import axios from "axios";

function ReviewsRight() {
  const [formData, setFormData] = useState({
    name: "",
    contact: "",
    email: "",
    requirement: "",
  });

  const handlechange=(e)=>{
    setformdata({...formdata,[e.target.name]:e.target.value});
  
     console.log(JSON.stringify(formdata));
     
  
  
   }
  return (
    <>
      <div className="container review-form-container" >
        <h1 style={{color:"black"}}>Review Box</h1>
        <form className="row g-3" onSubmit={handleSubmit}>
          <div className="col-md-6">
            <label
              htmlFor="inputName4"
              className="form-label review-form-label"
            >
              Name
            </label>
            <input
              type="text"
              className="form-control"
              id="name"
              value={formData.name}
              onChange={handleChange}
              style={{width:"200%"}}
            />
          </div>
          <div className="col-md-6">
            <label
              htmlFor="inputContact4"
              className="form-label review-form-label"
            >
              Contact
            </label>
            <input
            style={{width:"200%"}}
              type="number"
              className="form-control"
              id="contact"
              value={formData.contact}
              onChange={handleChange}
            />
          </div>
          <div className="col-md-12">
            <label
              htmlFor="inputEmail4"
              className="form-label review-form-label"
            >
              Email
            </label>
            <input
            style={{width:"95%"}}
              type="email"
              className="form-control"
              id="email"
              value={formData.email}
              onChange={handleChange}
            />
          </div>
          <div className="col-md-12">
            <label
              htmlFor="inputOpinion4"
              className="form-label review-form-label"
            >
              Your Requirements
            </label>
            <input
             style={{width:"95%",height:"100px"}}
              type="text"
              className="form-control review-form-input"
              id="opinion"
              value={formData.opinion}
              onChange={handleChange}
            />
          </div>
          <div className="col-12 review-btn">
            <button  type="submit" className="btn btn-outline-dark">
              Get an Instant Quote
            </button>
            
          </div>
        </form>
        
      </div>
    </>
  );
}

export default ReviewsRight;
