import{data} from"./data";
import React,{useEffect,useState} from 'react';
 function ProductList() {
    const [selectedFilters, setSelectedFilters] = useState([]);
    const [filteredItems, setFilteredItems] = useState(data);
  
    let filters = ["Mafia", "Anime", "Cars",];
  
    const handleFilterButtonClick = (selectedCategory) => {
      if (selectedFilters.includes(selectedCategory)) {
        let filters = selectedFilters.filter((el) => el !== selectedCategory);
        setSelectedFilters(filters);
      } else {
        setSelectedFilters([...selectedFilters, selectedCategory]);
      }
    };
  
    useEffect(() => {
      filterItems();
    }, [selectedFilters]);
  
    const filterItems = () => {
      if (selectedFilters.length > 0) {
        let tempItems = selectedFilters.map((selectedCategory) => {
          let temp = data.filter((item) => item.category === selectedCategory);
          return temp;
        });
        setFilteredItems(tempItems.flat());
      } else {
        setFilteredItems([...data]);
      }
    };
  
    return (
      <div>
        <div className="buttons-container">
          {filters.map((category, idx) => (
            <button
              onClick={() => handleFilterButtonClick(category)}
              className={`button ${
                selectedFilters?.includes(category) ? "active" : ""
              }`}
              key={`filters-${idx}`}
            >
              {category}
            </button>
          ))}
        </div>
  
        <div className="items-container">
          {filteredItems.map((item, idx) => (
            <div key={`data-${idx}`} className="item">
              <div class="row row-cols-1 row-cols-md-3 g-4">
  {p.map((data) => (
    <div class="col">
    <div class="card">
      <img src={data.image} class="card-img-top" alt={data.title}/>
      <div class="card-body">
        <h5 class="card-title">{data.title}</h5>
        <p class="card-text" style={{textAlign:"center"}}>{data.Price}</p>
       <div className='container'> <button className="btn btn-primary">Add to Cart</button></div>
      </div>
    </div>
  </div>
  ))}
</div>
            </div>
          ))}
        </div>
      </div>
    );
  }
   
    
 
export default ProductList;








































