import { useState, useEffect } from "react";
import { deleteproduct, getproduct } from "../Services/api";
import React from 'react';
import './products.css';
import EditProduct from "./EditProduct";
import { Link } from "react-router-dom";
export default function ProductView() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);
  const [showEdit, setShowEdit] = useState(false);
  const fetchData = async () => {
    try {
      const productDetail = await getproduct();
      setData(productDetail);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };
  const [selectedProduct, setSelectedProduct] = useState(null);

  const handleDeleteItem = async (productId) => {
    try {
      console.log("HandleDeleteItem");
      await deleteproduct(productId);
      // After deletion, fetch updated data
      fetchData();
    } catch (error) {
      console.error("Error deleting item:", error);
    }
  };
  const handleEdit = (productId) => {
    const productToEdit = data.find((item) => item.id === productId);
    setSelectedProduct(productToEdit);
    setShowEdit(true);
  };

  return (
    <div className="container">
      <div className="row row4">
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {data.map((item) => (
            <div className="col col-md-3" style={{ padding: "15px" }} key={item.id}>
              <div className="card">
                <img src={item.img} className="card-img-top cardimg" alt={item.title} />
                <div className="card-body">
                  <h5 className="card-title" style={{ textAlign: "center" }}>{item.title}</h5>
                  <p className="card-text" style={{ textAlign: "center" }}>{item.price}</p>
                  <div className="container">
                    <button className="btn btn-outline-dark" onClick={() => handleDeleteItem(item.id)}>Delete</button>
                   <Link to={`/editproduct/${item.id}`}><button onClick={() => handleEdit(item.id)} className="btn btn-outline-dark" >Edit</button></Link>
                    {/* <button className="btn btn-outline-dark" onClick={() => handleEdit(item.id)}>Edit</button> */}

                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
