import React, { useState } from 'react';
import { data } from './data';
import './productlist.css';

const ProductList = () => {
  const [selectedCategory, setSelectedCategory] = useState('');
  const [productQuantities, setProductQuantities] = useState({}); // Initialize product quantities state.

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleQuantityChange = (productId, quantity) => {
    setProductQuantities({
      ...productQuantities,
      [productId]: quantity,
    });
  };

  const getQuantityForProduct = (productId) => {
    return productQuantities[productId] || 0;
  };

  // Filter products based on the selected category.
  const filteredProducts = selectedCategory
    ? data.filter((product) => product.category === selectedCategory)
    : data;

  return (
    <div>
      <div className='container'>
        <button className='filterbtn' onClick={() => handleCategoryChange('Mafia')}>
          Mafia
        </button>
        <button className='filterbtn' onClick={() => handleCategoryChange('Anime')}>
          Anime
        </button>
        <button className='filterbtn' onClick={() => handleCategoryChange('Cars')}>
          Cars
        </button>
        <button className='filterbtn' onClick={() => handleCategoryChange('')}>
          Show All
        </button>
      </div>
      <h2 className='container' style={{ color: 'black' }}>
        {selectedCategory ? `${selectedCategory} Products` : 'All Products'}
      </h2>
      <div className='container'>
        <div className='row row4'>
          <div className='row row-cols-1 row-cols-md-3 g-4'>
            {filteredProducts.map((product) => (
              <div className='col col-md-3' key={product.id} style={{ padding: '15px' }}>
                <div className='card cardsize' style={{ height: '600px', width: '300px' }}>
                  <img src={product.image} className='card-img-top cardimg' alt={product.title} />
                  <div className='card-body'>
                    <h5 className='card-title'>{product.title}</h5>
                    <p className='card-text' style={{ textAlign: 'center' }}>
                      Rs. {product.Price}
                    </p>
                    <div className='container'>
                      <label>Quantity:</label>
                      <input
                        type='number'
                        value={getQuantityForProduct(product.id)}
                        onChange={(e) => handleQuantityChange(product.id, e.target.value)}
                      />
                      <button className='btn btn-primary'>Add to Cart</button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductList;
