import { useState,useEffect } from "react";
import { getproduct } from "../Services/api"
import React from 'react'

export default function Productview() {
  
const [data, setData]=useState([]);
useEffect(()=>{
    fetchdata();
},[]);

const fetchdata=async()=>{
    const productdetail= await getproduct();
    console.log("...");
setData(productdetail);
}
  
  
    return (
    <div>
      {
        data.map(item=>(
            <div key={item.id}>
                
          
           <div class="col col-md-3 " style={{padding:"15px"}}>
                <div class="card">
                  <img src={item.image} class="card-img-top cardimg"  />
                  <div class="card-body">
                    <h5 class="card-title">{item.name}</h5>
                    <p class="card-text" style={{ textAlign: "center" }}>{item.price}</p>
                    <div className='container'> <button className="btn btn-outline-dark">Add to Cart</button></div>
                  </div>
                </div>
              </div>
            </div>
        ))
      }
    </div>
  )
}
