import axios from "axios";
const url="http://localhost:3000/egs";
const api=axios.create({
    url:url
});
export const  addproduct= async(productdata)  =>{
return await axios.post(`${url}/egs`,productdata);
 }
 

 export const getproduct=async()=>{
    const response= await axios.get("/viewproducts");
    console.log("RUNNING");
    return response.data;
    
 }
 export const deleteproduct = async (productid) => {
    try {
        const response = await api.delete(`/delete-product/${productid}`);
        return response.data;
    } catch (error) {
        if (error.response) {
            // The request was made, but the server responded with a status code outside of 2xx
            console.error("Server responded with an error:", error.response.data);
            console.error("Status code:", error.response.status);
        } else if (error.request) {
            // The request was made but no response was received
            console.error("No response received:", error.request);
        } else {
            // Something happened in setting up the request that triggered an error
            console.error("Error setting up the request:", error.message);
        }
        throw error; // Propagate the error for further handling
    }
};
