import { useState,useEffect } from "react";
import { getproduct } from "../Services/api"
import React from 'react'
import './products.css';
export default function Productview() {
  
const [data, setData]=useState([]);
useEffect(()=>{
    fetchdata();
},[]);

const fetchdata=async()=>{
    const productdetail= await getproduct();
    console.log("...");
setData(productdetail);
}
  const handledeleteitem= async 
  
    return (
        <>
        <div className="container">
          <div className="row row4">
            <div class="row row-cols-1 row-cols-md-3 g-4">
              {data.map((item) => (
                <div class="col col-md-3 " style={{padding:"15px"}} key={item.id}>
                  <div class="card">
                    <img src={item.img} class="card-img-top cardimg" alt={item.name} />
                    <div class="card-body">
                      <h5 class="card-title"style={{ textAlign: "center" }}>{item.title}</h5>
                      <p class="card-text" style={{ textAlign: "center" }}>{item.price}</p>
                      <div className='container'> <button className="btn btn-outline-dark" onClick={()=>handledeleteitem(item.id)}>Delete</button></div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
  
      </>
  )
}
