import React, { useState } from 'react';
import { data } from './data';

const ProductList = () => {
  const [selectedCategory, setSelectedCategory] = useState(''); // Initialize the selectedCategory state to an empty string.

  // Filter products based on the selected category.
  const filteredProducts = selectedCategory
    ? data.filter(product => product.category === selectedCategory)
    : data; // If no category is selected, show all products.

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div>
      <h1>Product Categories</h1>
      <div >
        <button onClick={() => handleCategoryChange('Mafia')}>Mafia</button>
        <button onClick={() => handleCategoryChange('Anime')}>Anime</button>
        <button onClick={() => handleCategoryChange('Cars')}>Cars</button>
        <button onClick={() => handleCategoryChange('')}>Show All</button> {/* Add a "Show All" button */}
      </div>
      <h2>
        {selectedCategory ? `${selectedCategory} Products` : 'All Products'} {/* Display the selected category or "All Products" */}
      </h2>
      <div className='container'>
        <div className="row row4">
          <div className="row row-cols-1 row-cols-md-3 g-4">
            {filteredProducts.map((product) => (
              <div className="col col-md-3" key={product.id} style={{ padding: "15px" }}>
                <div className="card cardsize" style={{ height: "600px", width: "300px" }}>
                  <img src={product.image} className="card-img-top cardimg" alt={product.title} />
                  <div className="card-body">
                    <h5 className="card-title">{product.title}</h5>
                    <p className="card-text" style={{ textAlign: "center" }}>{product.Price}</p>
                    <div className='container'><button className="btn btn-primary">Add to Cart</button></div>
                    <div> </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductList;
