import React from 'react';
import styled from 'styled-components';

const Banner = styled.div`
  background-color: #f44336;
  padding: 20px;
  text-align: center;
`;

const Heading = styled.h1`
  color: white;
  font-size: 30px;
  margin-bottom: 10px;
`;

const Subheading = styled.h2`
  color: white;
  font-size: 20px;
  margin-bottom: 10px;
`;

const Service = styled.div`
  display: inline-block;
  width: 33%;
  padding: 10px;
  text-align: center;
`;

const ServiceIcon = styled.img`
  width: 50px;
  height: 50px;
  margin-bottom: 10px;
`;

const ServiceTitle = styled.h3`
  color: white;
  font-size: 16px;
`;

const ServiceDescription = styled.p`
  color: white;
  font-size: 14px;
`;

const BannerComponent = () => {
  return (
    <Banner>
      <Heading>Our Services</Heading>
      <Subheading>Project Consultation, Expert Team, Deadline Oriented</Subheading>
      <div>
        <Service>
          <ServiceIcon src="/assets/project-consultation.png" />
          <ServiceTitle>Project Consultation</ServiceTitle>
          <ServiceDescription>To help shortlist the right visibility solution for your diverse requirements.</ServiceDescription>
        </Service>
        <Service>
          <ServiceIcon src="/assets/expert-team.png" />
          <ServiceTitle>Expert Team</ServiceTitle>
          <ServiceDescription>Excellence in design, fabrication and execution with transparency and accountability.</ServiceDescription>
        </Service>
        <Service>
          <ServiceIcon src="/assets/deadline-oriented.png" />
          <ServiceTitle>Deadline Oriented</ServiceTitle>
          <ServiceDescription>Customer satisfaction through our great services & quality. Real value for money.</ServiceDescription>
        </Service>
      </div>
    </Banner>
  );
};

export default BannerComponent;
