import { useState, useEffect } from "react";
import { deleteproduct, getproduct } from "../Services/api"
import React from 'react'
import './products.css';
export default function Productview() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetchdata();
  }, []);

  const fetchdata = async () => {
    try {
      const productdetail = await getproduct();
      if (Array.isArray(productdetail)) {
        setData(productdetail);
      } else {
        setData([]); // If productdetail is not an array, set an empty array
      }
    } catch (error) {
      console.error("Error fetching data:", error);
      setData([]); // Set an empty array in case of an error
    }
  };

  const handledeleteitem = async (productid) => {
    try {
      await deleteproduct(productid);
      // Fetch updated data after deletion
      fetchdata();
    } catch (error) {
      console.error("Error deleting product:", error);
    }
  };

  return (
    <div className="container">
      <div className="row row4">
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {Array.isArray(data) && data.length > 0 ? (
            data.map((item) => (
              <div className="col col-md-3" style={{ padding: "15px" }} key={item.id}>
                <div className="card">
                  <img src={item.img} className="card-img-top cardimg" alt={item.title} />
                  <div className="card-body">
                    <h5 className="card-title" style={{ textAlign: "center" }}>{item.title}</h5>
                    <p className="card-text" style={{ textAlign: "center" }}>{item.price}</p>
                    <div className="container">
                      <button className="btn btn-outline-dark" onClick={() => handledeleteitem(item.id)}>Delete</button>
                    </div>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <p>No data available</p>
          )}
        </div>
      </div>
    </div>
  );
}
