import React, { useState } from 'react';
import products from './data'; // Import the product data from data.js

const ProductList = () => {
  const [selectedCategory, setSelectedCategory] = useState('Mafia');

  const filteredProducts = products.filter(product => product.category === selectedCategory);

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div>
      <h1>Product Categories</h1>
      <div>
        <button onClick={() => handleCategoryChange('Mafia')}>Mafia</button>
        <button onClick={() => handleCategoryChange('Anime')}>Anime</button>
        <button onClick={() => handleCategoryChange('Cars')}>Cars</button>
      </div>
      <h2>{selectedCategory} Products</h2>
      <div className="row">
        {filteredProducts.map((Canvasdata) => (
          <div key={Canvasdata.id} className="col">
            <div className="card">
              <img src={Canvasdata.image} className="card-img-top" alt={Canvasdata.title} />
              <div className="card-body">
                <h5 className="card-title">{Canvasdata.title}</h5>
                <p className="card-text" style={{ textAlign: "center" }}>{Canvasdata.Price}</p>
                <div className="container"><button className="btn btn-primary">Add to Cart</button></div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;

















