function ProductList() {


// Define the categories
const categories = ['Mafia', 'Anime', 'Cars'];

// Create a function to filter the products by category
function filterProductsByCategory(category) {
    return data.filter(product => product.category === category);
}

// Create a function to render the products for a given category
function renderProductsForCategory(category) {
    const products = filterProductsByCategory(category);
    // Render the products however you want, for example:
    <div class="row row-cols-1 row-cols-md-3 g-4">
  {data.map((data) => (
    <div class="col">
    <div class="card">
      <img src={data.image} class="card-img-top" alt={data.title}/>
      <div class="card-body">
        <h5 class="card-title">{data.title}</h5>
        <p class="card-text" style={{textAlign:"center"}}>{data.Price}</p>
       <div className='container'> <button className="btn btn-primary">Add to Cart</button></div>
      </div>
    </div>
  </div>
  ))}
</div>
}

// Render the products for each category
categories.forEach(category => renderProductsForCategory(category));
}
export default ProductList;
















