import {DataContext} from '../App';
import exhibitdata from "../components/exhibsrc";


const { useState, createContext, useContext } = require("react");


function Exhibition() {
    const{productData, setProductData}=useContext(DataContext);
  
    return (
    
    <div>
      <h1>Exhibition</h1>
      <div className="container">
        <div className="row row4">
          <div class="row row-cols-1 row-cols-md-3 g-4">
            {exhibitdata.map((Canvasdata) => (
              <div class="col col-md-3 " style={{padding:"15px"}}>
                <div class="card">
                  <img src={Canvasdata.image} class="card-img-top cardimg" alt={Canvasdata.title} />
                 
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
export default Exhibition;
