import React, { useState,useEffect } from 'react';
import { Table, Column } from "react-table";
import '../components/orders.css'
function Orders(){
    const [products, setProducts] = useState([]);

  // Use a fetch request to get the products from the data.js file.
  const fetchProducts = async () => {
    const response = await fetch("/data.js");
    const data = await response.json();
    setProducts(data);
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // Allow editing the product title and price.
  const handleEditProduct = (product) => {
    // Update the product's title and price in the state.
    setProducts((prevProducts) => {
      const updatedProducts = prevProducts.map((p) => {
        if (p.id === product.id) {
          p.title = product.title;
          p.price = product.price;
        }
        return p;
      });
      return updatedProducts;
    });
  };

  return (
    <Table data={products}>
      <Column header="Title" accessor="title" editable />
      <Column header="Price" accessor="price" editable />
      <Column
        header="Actions"
        cellRenderer={({ row }) => (
          <button onClick={() => handleEditProduct(row.original)}>Edit</button>
        )}
      />
    </Table>
  );
}

export default Orders;









